import React from 'react';
import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
html {
  box-sizing: border-box;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}  

body {
  margin: 0;
  background-color: #f0f0f0;
  font-family: Arial, sans-serif;
  font-size: 20px;
  color: black
}

a {
  text-decoration: none;
  color: inherit;
}

ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

h1, h2, h3,  {
  padding: 0;
  margin: 0;
}

`;

function App() {
  return (
    <div>
      <GlobalStyle/>
        <div className="App">
            Hello react
        </div>
    </div>
  );
}

export default App;
