import React from 'react';
import styled from  'styled-components';
import logoImg from '../image/logo.svg'

const NavbarStyled = styled.header`
    position: fixed;
    top: 0;
    left: 0;
    z-index: 999;
    height: 80px;
    width: 100vw;
    display: flex;
    justify-content: space-between;
    padding: 15px;
    background-color: #299B01;
    color: white;
`;
const Logo = styled.div `
    display: flex;
    align-items: center;
`;
const H1 = styled.h1`
    font-size: 24px;
    margin-left: 15px;
`;
const ImgLogo = styled.img`
    width: 50px;
`;
const Btn = styled.button``;

export const NavBar = () => (
    <NavbarStyled>
        <Logo>
            <div>
                <img src={logoImg} alt="logo"/>
                <h1>MrDonald</h1>
            </div>
            <div>
                <button>Войти</button>
            </div>
        </Logo>
    </NavbarStyled>
)
